{
    "$schema": "https://developer.microsoft.com/json-schemas/sp/view-formatting.schema.json",
    "hideSelection": true,
    "hideListHeader": true,
    "rowFormatter": {
      "debugMode": true,
      "elmType": "div",
      "style": {
        "padding": "12px",
        "overflow": "hidden",
        "border-radius": "2px",
        "box-shadow": "0 1.6px 3.6px 0 #dddddd, 0 0.3px 0.9px 0 #e3e3e3",
        "margin-top": "12px",
        "margin-bottom": "12px",
        "min-width": "350px",
        "border": "=if(Number([$SumOfGradeWeights]) &lt;= 0 , '2px solid transparent' , if((Number([$Grade]) * 100 / Number([$SumOfGradeWeights]) &lt; 60 ,'2px solid #FFCCCC','2px solid transparent'))"
      },
      "children": [
        {
          "elmType": "div",
          "style" : {
            "width": "100%"
          },
          "children": [
            {
              "elmType": "div",
              "style": {
                "border": "0",
                "cursor": "pointer"
              },
              "children": [
                {
                  "elmType": "img",
                  "attributes": {
                    "src": "= @currentWeb + '/_layouts/15/userphoto.aspx?size=L&amp;accountname=' + [$Student.email]"
                  },
                  "style": {
                    "max-width": "100%",
                    "width": "80px",
                    "margin-left": "4px",
                    "margin-right": "16px",
                    "display": "inline-block",
                    "border-radius": "4px"
                  }
                },
                {
                  "elmType": "div",
                  "style": {
                    "text-align": "left",
                    "vertical-align": "top",
                    "max-width": "600px",
                    "display": "inline-block"
                  },
                  "children": [
                    {
                      "elmType": "div",
                      "txtContent": "[$Student.title]",
                      "style": {
                        "font-size": "1.4em",
                        "padding-bottom": "4px",
                        "white-space": "normal"
                      }
                    },
                    {
                      "elmType": "div",
                      "txtContent": "[$Student.jobTitle]",
                      "style": {
                        "padding-bottom": "4px",
                        "white-space": "normal"
                      }
                    },
                    {
                      "elmType": "div",
                      "txtContent": "[$Student.department]",
                      "style": {
                        "padding-bottom": "4px",
                        "white-space": "normal"
                      }
                    },
                    {
                      "elmType": "div",
                      "txtContent": "[$Student.email]",
                      "style": {
                        "padding-bottom": "4px",
                        "white-space": "normal"
                      }
                    }
                  ]
                }
              ]
            },
            {
              "elmType": "div",
              "style": {
                "width" : "100%",
                "border": "1px solid grey",
                "border-radius": "4px",
                "display": "=if( (Number([$SumOfGradeWeights]) &lt;= 0) ,'none', '')"
              },
              "children": [
                {
                  "elmType": "div",
                  "attributes": {
                    "class": "sp-field-dataBars"
                  },
                  "style": {
                    "width": "=if((Number([$Grade]) * 100 / Number([$SumOfGradeWeights])) &gt; 100, '100%', if( Number([$SumOfGradeWeights]) &lt;=0, '0%', toString(Number([$Grade]) * 100 / Number([$SumOfGradeWeights]) ) + '%')",
                    "white-space": "normal",
                    "font-size": "xx-large",
                    "max-height": "100px",
                    "overflow": "hidden",
                    "text-overflow": "ellipsis",
                    "box-sizing": "border-box",
                    "color": "=if((Number([$Grade]) * 100 / Number([$SumOfGradeWeights]) &lt;= 90,'', '#007700')"
                  },
                  "txtContent": "=if( (Number([$Grade]) &lt;= 0) || (Number([$SumOfGradeWeights]) &lt;= 0) , '', toString(Number([$Grade]) * 100 / Number([$SumOfGradeWeights])) + '%')"
              }                
              ]
            },
            {
              "elmType": "div",
              "style": {
                "padding-top": "10px",
                "padding-bottom": "10px"
              },
              "children": [
                {
                  "elmType": "a",
                  "style": {
                    "cursor": "pointer",
                    "padding": "6px",
                    "border-radius": "2px",
                    "box-shadow": "5px 10px 18px #999999",
                    "margin-right": "5px"
                  },
                  "attributes": {
                    "target": "_blank",
                    "href": "= @currentWeb + '/Lists/Assignment%20Grades/AllItems.aspx?useFiltersInViewXml=1&amp;FilterField1=Student&amp;FilterType1=User&amp;FilterValue1=' + [$Student.title]"
                  },
                  "txtContent": "Assignments and Grades"
                },
                {
                  "elmType": "a",
                  "style": {
                    "cursor": "pointer",
                    "padding": "6px",
                    "border-radius": "2px",
                    "box-shadow": "5px 10px 18px #999999",
                    "margin-right": "5px"
                  },
                  "attributes": {
                    "target": "_blank",
                    "href": "= @currentWeb + '/' + [$StudentFolder]"
                  },
                  "txtContent": "Turn it in folder"
                },
                {
                  "elmType": "a",
                  "style": {
                    "cursor": "pointer",
                    "padding": "6px",
                    "border-radius": "2px",
                    "box-shadow": "5px 10px 18px #999999",
                    "margin-right": "5px"
                  },
                  "attributes": {
                    "target": "_blank",
                    "href": "= @currentWeb + '/SitePages/Student-Page.aspx?Student=' + [$Student.title]"
                  },
                  "txtContent": "Student Overview"
                }
              ]
            }
          ]
        }
      ]
    }
  }

{
    "$schema": "https://developer.microsoft.com/json-schemas/sp/view-formatting.schema.json",
    "hideSelection": true,
    "hideListHeader": true,
    "rowFormatter": {
        "debugMode": true,
        "elmType": "div",
        "style": {
            "padding": "12px",
            "overflow": "hidden",
            "border-radius": "2px",
            "box-shadow": "0 1.6px 3.6px 0 #dddddd, 0 0.3px 0.9px 0 #e3e3e3",
            "border": "=if( ([$Due_x0020_Date] &gt; (@now + 86400000)) || ([$turnedIn] ) ,'4px solid transparent','4px solid #ffeaea')",
            "margin-bottom": "12px"
        },
        "children": [
            {
                "elmType": "div",
                "style": {
                    "width": "100%",
                    "text-align": "left"
                },
                "children": [
                    {
                        "elmType": "a",
                        "style": {
                            "font-size": "16px",
                            "font-weight": "600",
                            "padding-bottom": "8px",
                            "display": "block",
                            "text-decoration": "none"
                        },
                        "attributes": {
                            "target": "_blank",
                            "href": "= @currentWeb + '/Lists/Assignments/DispForm.aspx?env=WebView&amp;ID=' + [$Assignment.lookupId]"
                        },
                        "txtContent": "[$Assignment.lookupValue]"
                    },
                    {
                        "elmType": "div",
                        "txtContent": "[$Assignment_x003a_Brief_x0020_Des]",
                        "style": {
                            "padding-bottom": "4px"
                        }
                    },
                    {
                        "elmType": "div",
                        "style": {
                            "margin-bottom": "5px"
                        },
                        "txtContent" : "= 'Due : ' + toLocaleDateString([$Due_x0020_Date])"
                    },
                    {
                        "elmType": "div",
                        "style": {
                            "width" : "100%",
                            "border": "1px solid grey",
                            "border-radius": "4px",
                            "display": "=if( (Number([$Grade]) &lt;= 0) ,'none', '')"
                          },            
                        "children": [
                            {
                                "elmType": "div",
                                "attributes": {
                                    "class": "sp-field-dataBars"
                                },
                                "style": {
                                    "box-sizing": "border-box",                
                                    "width": "=if((Number([$Grade]) * 100 / Number([$Assignment_x003a_Weight])) &gt; 100, '100%', if( Number([$Assignment_x003a_Weight]) &lt;=0, '0%', toString(Number([$Grade]) * 100 / Number([$Assignment_x003a_Weight]) ) + '%')",
                                    "padding": "0px"
                                },
                                "children": [
                                    {
                                        "elmType" : "div",
                                        "style": {
                                            "padding": "8px",
                                            "text-overflow": "ellipsis",
                                            "overflow": "hidden"
                                        },
                                        "txtContent": "=if( [$Grade] == '', '', [$Grade] + '/' + [$Assignment_x003a_Weight] + ' = ' + toString(Number([$Grade]) * 100 / Number([$Assignment_x003a_Weight])) + '%')"
                                    }
                                ]
                            }                            
                        ]
                    },

                    {
                        "elmType": "a",
                        "attributes": {
                            "href": "= @currentWeb + '/' + [$TurnItInFolder]",
                            "target": "_blank"
                        },
                        "txtContent": "=if([$turnedIn] , 'Assignment folder...' , 'Turn assignment in...')",
                        "style": {
                            "display": "inline-block",
                            "cursor": "pointer",
                            "margin-top": "4px",
                            "padding": "4px",
                            "border": "1px solid",
                            "background-color": "=if([$turnedIn] ,'#daffda','#ffdada')",
                            "border-color": "=if([$turnedIn] ,'green','red')",
                            "border-radius": "2px",
                            "box-shadow": "0 0 0 0 #ffffff",
                            "color": "black"
                        }
                    },
                    {
                        "elmType" : "button",
                        "customRowAction": {
                            "action": "editProps"
                          },
                        "txtContent": "Grade this assignment...",
                        "style": {
                            "display": "=if([$Student.email] == @me, 'none', 'inline-block')",
                            "margin-left": "4px",
                            "padding": "3px",
                            "cursor": "pointer"
                        }
                    }
                ]
            }
        ]
    }
}

