                                    "operator": "+",
                                    "operands": [
                                      {
                                        "operator": "toString()",
                                        "operands": [
                                          {
                                            "operator": "/",
                                            "operands": [
                                              {
                                                "operator": "?",
                                                "operands": [
                                                  {
                                                    "operator": "==",
                                                    "operands": [
                                                      "[$Grade]",
                                                      ""
                                                    ]
                                                  },
                                                  0,
                                                  {
                                                    "operator": "*",
                                                    "operands": [
                                                      {
                                                        "operator": "Number()",
                                                        "operands": [
                                                          "[$Grade]"
                                                        ]
                                                      },
                                                      100
                                                    ]
                                                  }
                                                ]
                                              },
                                              {
                                                "operator": "Number()",
                                                "operands": [
                                                  "[$Assignment_x003a_Weight]"
                                                ]
                                              }
                                            ]
                                          }
                                        ]
                                      },
                                      "%"
                                    ]
                                  }
                                ]
                              },
                            "padding": "0px"
                        },
                        "children": [
                            {
                                "elmType" : "div",
                                "style": {
                                    "padding": "8px"
                                },
                                "txtContent": {
                                    "operator": "?",
                                    "operands": [
                                      {
                                        "operator": "==",
                                        "operands": [
                                          "[$Grade]",
                                          ""
                                        ]
                                      },
                                      "",
                                      {
                                        "operator": "+",
                                        "operands": [
                                          {
                                            "operator": "+",
                                            "operands": [
                                              {
                                                "operator": "+",
                                                "operands": [
                                                  {
                                                    "operator": "+",
                                                    "operands": [
                                                      {
                                                        "operator": "+",
                                                        "operands": [
                                                          "[$Grade]",
                                                          " / "
                                                        ]
                                                      },
                                                      "[$Assignment_x003a_Weight]"
                                                    ]
                                                  },
                                                  " = "
                                                ]
                                              },
                                              {
                                                "operator": "toString()",
                                                "operands": [
                                                  {
                                                    "operator": "/",
                                                    "operands": [
                                                      {
                                                        "operator": "*",
                                                        "operands": [
                                                          {
                                                            "operator": "Number()",
                                                            "operands": [
                                                              "[$Grade]"
                                                            ]
                                                          },
                                                          100
                                                        ]
                                                      },
                                                      {
                                                        "operator": "Number()",
                                                        "operands": [
                                                          "[$Assignment_x003a_Weight]"
                                                        ]
                                                      }
                                                    ]
                                                  }
                                                ]
                                              }
                                            ]
                                          },
                                          "%"
                                        ]
                                      }
                                    ]
                                  }
                            }
                        ]
                    },
                    {
                        "elmType": "a",
                        "attributes": {
                            "href": "= @currentWeb + '/' + [$TurnItInFolder]",
                            "target": "_blank"
                        },
                        "txtContent": "=if([$turnedIn] , 'Assignment folder...' , 'Turn assignment in...')",
                        "style": {
                            "display": "inline-block",
                            "cursor": "pointer",
                            "margin-top": "4px",
                            "padding": "4px",
                            "border": "1px solid",
                            "background-color": "=if([$turnedIn] ,'#daffda','#ffdada')",
                            "border-color": "=if([$turnedIn] ,'green','red')",
                            "border-radius": "2px",
                            "box-shadow": "0 0 0 0 #ffffff",
                            "color": "black"
                        }
                    },
                    {
                        "elmType" : "button",
                        "customRowAction": {
                            "action": "editProps"
                          },
                        "txtContent": "Grade this assignment...",
                        "style": {
                            "display": "=if([$Student.email] == @me, 'none', 'inline-block')",
                            "margin-left": "4px",
                            "padding": "3px",
                            "cursor": "pointer"
                        }
                    }
                ]
            }
        ]
    }
}
